mapper.select:
		SELECT c.*,f.fname 
		FROM commodity c,fine f 
		WHERE c.finid = f.finid

			and cname like "%"#{cname}"%"


			and c.finid = #{finid}


			and cweight = #{cweight}


			and month = #{month}


            and cstatus = #{cstatus}

		order by comid DESC

            limit #{firstRow},#{pageSize}

mapper.update:
update commodity
set cstatus = 0
where comid = #{comid}

update ssms.commodity
set ssms.commodity.cstatus = 1
where comid = #{comid}
